"use strict";function getData(){farming_id=location.href.split("=")[1];var t="select * from farming where id="+farming_id;getPHP("src/php/query.php?sql="+t).then(function(t){getData_input(JSON.parse(t))},function(t){console.error("出错了",t)})}function getData_input(t){console.log(t);var n;n='\n    <p>\n        <img src="src/img/farming/'+t[0].img+'" align="right" hspace="20" vspace="20" title="'+t[0].title+'">\n        '+t[0].intro+"\n    </p>\n    ",$("#farming-show").html(n)}getData();