"use strict";function str_dea_updatenum(t,e){e=parseInt(e)+1;getPHP("src/php/querya.php?sql="+("UPDATE strategy SET num ="+e+" WHERE id ="+t)).then(function(t){})}function str_dea_search(){$(".act_search").on("click",function(){var t=$(".search_val").val();window.sessionStorage.setItem("str_search",t),$(this).parent().attr("href","strategy.html?title="+t)})}function str_dea_input_color(){$(".search_val").on("focusin",function(){$(".search").css("border","1px solid greenyellow")}).on("focusout",function(){$(".search").css("border","1px solid gainsboro")})}function str_dea_each_item(){getPHP("src/php/query.php?sql=SELECT * FROM strategy").then(function(t){for(var n=JSON.parse(t),e="",s=0;s<3;s++)e+=' \n            <li><a href="strategy_detail.html?id='+n[s].id+"\" rel='"+s+"'>"+n[s].title+"</a></li>\n            ";$(".echo_item").html(e),$(".echo_item a").on("click",function(){var t=$(this).attr("href").split("=")[1],e=n[$(this).attr("rel")].num;null==window.sessionStorage.getItem("str_num"+t)&&(str_dea_updatenum(t,e),window.sessionStorage.setItem("str_num"+t,e))})})}function str_detail_info(){var t=window.location.href.split("=")[1];getPHP("src/php/query.php?sql="+("SELECT * FROM strategy where id="+t)).then(function(t){var e=JSON.parse(t);console.log(e),$("head title").text(e[0].title+"详情页面");for(var n="",s=0;s<e.length;s++)n+=" \n            <p>"+e[s].title+'</p>\n            <div class="str_time">\n                <span>'+e[s].time+'</span><i class="fa fa-eye fa"></i><span>'+e[s].num+'</span>\n            </div>\n            <hr>\n            <div class="str_content">\n                <img src="./src/img/'+e[s].img+'" alt="">\n                <div class="str_text">\n                '+e[s].intro+"\n                </div>\n            </div>\n            <hr>\n            ";$(".str_con").html(n)})}str_dea_search(),str_dea_input_color(),str_dea_each_item(),str_detail_info();